  const [userid,setUserid] = useState(null)

  useEffect(() =>{
    getlist()
  },[])

  function getlist(){
    fetch("https://mock-login-todo-api.herokuapp.com/todos/kashif").then((result) =>{
      result.json().then((res) =>{
        setList(res);
        setTodo(res[0].todo)
        setUserid(res[0].id)
      })
    })
  }
  console.warn(list,"list")
  function deletelist(id){
    // alert(`https://mock-login-todo-api.herokuapp.com/todos/kashif/${id}`)
    fetch(`https://mock-login-todo-api.herokuapp.com/todos/kashif/${id}`,{
      method: 'DELETE'
    }).then((result) =>{
      result.json().then((res) =>{
        console.warn(res)
        getlist()
        
      })
    })
    
  }
  function updatelist(id){
   console.warn(list[id-1])
   let item = list[id-1]
   setTodo(item.title)
   setUserid(item.id)

  }
  function update(){
    let item = {todo,userid}
    fetch(`https://mock-login-todo-api.herokuapp.com/todos/kashif/${userid}`,{
      method: 'PUT',
      headers:{
        'Accept':'application/json',
        'Content-Type': 'application/json'
      },
      body:J
    }).then((result) =>{
      result.json().then((res) =>{
        console.warn(res)
        getlist()
        
      })
    })
     
  }

  
// console.log(todo);
  return (
    <>
        <div className=''>
        <h1> TODO LIST</h1>
             <input type="text" name="list" value={todo} onChange={((e) => setTodo(e.target.value))}  /> <br /> <br />
              
              <input type="button" value="add Todo" className='btn btn-primary' /> <nbsp/> <nbsp/><nbsp/><nbsp/>
              <input type="button" value="clear Todo" className='btn btn-primary'  />
              <input type="button" value="update List" onClick={update}  className='btn btn-primary'  />
              
        </div>
        
        <table border={1}>
        <tr>
          <th>id </th>
          <th>title </th>
          <th>checked </th>
        </tr>
        {list.map(function(value,indx){

return(
    <tr>
        <td> {value.id} </td>
        <td> {value.title} </td>
        <td>{value.checked} </td>
        <tb> <button onClick={() => deletelist(value.id)}> delete</button> </tb>
        <tb> <button onClick={() => updatelist(value.id)}> update</button> </tb>
      
    </tr>
)

})}

        </table>
        
    </>
  )
}

export default TodoList
