    <>
        <div className=''>
        <h1> TODO LIST</h1>
             <input type="text" name="list" value={todo} onChange={handleTodo}  /> <br /> <br />
              
              <input type="button" value="addTodo" onClick={addlist} className='btn btn-primary' /> <nbsp/> <nbsp/><nbsp/><nbsp/>
              <input type="button" value="clearTodo" className='btn btn-primary'  />
