import React, { useState,useReducer, useEffect } from 'react'

const item= [];

function items(){

}

function TodoList() {
  var [todo,setTodo] =  useState("")
  const [state, dispatch] = useReducer(items,item);

  var [list,setList] = useState([])

  useEffect(() =>{
    getlist()
  },[])

  function getlist(){
    fetch("https://mock-login-todo-api.herokuapp.com/todos/kashif").then((result) =>{
      result.json().then((res) =>{
        setList(res);
        setTodo(res[0].todo)
      })
    })
  }
  console.warn(list,"list")
  function deletelist(id){
    // alert(`https://mock-login-todo-api.herokuapp.com/todos/kashif/${id}`)
    fetch(`https://mock-login-todo-api.herokuapp.com/todos/kashif/${id}`,{
      method: 'DELETE'
    }).then((result) =>{
      result.json().then((res) =>{
        console.warn(res)
        getlist()
        
      })
    })
    
  }
  function updatelist(id){
   console.warn(list[id-1])

  }

  
// console.log(todo);
  return (
    <>
        <div className=''>
        <h1> TODO LIST</h1>
             <input type="text" name="list" value={todo} onChange={((e) => setTodo(e.target.value))}  /> <br /> <br />
              
              <input type="button" value="add Todo" className='btn btn-primary' /> <nbsp/> <nbsp/><nbsp/><nbsp/>
              <input type="button" value="clear Todo" className='btn btn-primary'  />
              
        </div>
        
        <table border={1}>
        <tr>
          <th>id </th>
          <th>title </th>
          <th>checked </th>
        </tr>
        {list.map(function(value,indx){

return(
    <tr>
        <td> {value.id} </td>
        <td> {value.title} </td>
        <td>{value.checked} </td>
        <tb> <button onClick={() => deletelist(value.id)}> delete</button> </tb>
        <tb> <button onClick={() => updatelist(value.id)}> update</button> </tb>
      
    </tr>
)

})}

        </table>
        
    </>
  )
}

export default TodoList
